mapper.select:
select * FROM product
p,categorysecond cs where p.csid = cs.csid and cs.cid = #{cid} LIMIT
#{begin},#{limit}

select * FROM product
p,categorysecond cs where p.csid = cs.csid and cs.csid = #{csid} LIMIT
#{begin},#{limit}

SELECT * FROM product p, category c, categorysecond cs
p.csid = cs.csid and cs.cid = c.cid
AND ( c.cname LIKE CONCAT(CONCAT('%',#{pc.type},'%'))
OR cs.csname LIKE CONCAT(CONCAT('%',#{pc.type},'%')) )
AND p.shop_price >= #{pc.minPrice}
AND p.shop_price <= #{pc.maxPrice}
AND p.pname LIKE CONCAT(CONCAT('%',#{pc.pname},'%'))
limit #{begin},#{limit}

SELECT count(*) FROM product p, category c, categorysecond cs
p.csid = cs.csid and cs.cid = c.cid
AND ( c.cname LIKE CONCAT(CONCAT('%',#{pc.type},'%')) OR cs.csname LIKE CONCAT(CONCAT('%',#{pc.type},'%')) )
AND p.shop_price >= #{pc.minPrice}
AND p.shop_price <= #{pc.maxPrice}
AND p.pname LIKE CONCAT(CONCAT('%',#{pc.pname},'%'))
